App = {
  web3Provider: null,
  bytecode: "",
  abi: [
    {
      "constant": true,
      "inputs": [
        {
          "name": "",
          "type": "uint256"
        }
      ],
      "name": "validators",
      "outputs": [
        {
          "name": "validator",
          "type": "address"
        },
        {
          "name": "hasValidated",
          "type": "bool"
        }
      ],
      "payable": false,
      "stateMutability": "view",
      "type": "function"
    },
    {
      "constant": false,
      "inputs": [],
      "name": "destroy",
      "outputs": [],
      "payable": false,
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "constant": true,
      "inputs": [],
      "name": "owner",
      "outputs": [
        {
          "name": "",
          "type": "address"
        }
      ],
      "payable": false,
      "stateMutability": "view",
      "type": "function"
    },
    {
      "constant": true,
      "inputs": [],
      "name": "minValidators",
      "outputs": [
        {
          "name": "",
          "type": "uint8"
        }
      ],
      "payable": false,
      "stateMutability": "view",
      "type": "function"
    },
    {
      "constant": true,
      "inputs": [
        {
          "name": "",
          "type": "uint256"
        }
      ],
      "name": "recipients",
      "outputs": [
        {
          "name": "recipient",
          "type": "address"
        },
        {
          "name": "percent",
          "type": "uint8"
        }
      ],
      "payable": false,
      "stateMutability": "view",
      "type": "function"
    },
    {
      "constant": false,
      "inputs": [
        {
          "name": "newOwner",
          "type": "address"
        }
      ],
      "name": "transferOwnership",
      "outputs": [],
      "payable": false,
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "constant": false,
      "inputs": [
        {
          "name": "_recipient",
          "type": "address"
        }
      ],
      "name": "destroyAndSend",
      "outputs": [],
      "payable": false,
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "name": "_recipients",
          "type": "address[]"
        },
        {
          "name": "_percentages",
          "type": "uint8[]"
        },
        {
          "name": "_validators",
          "type": "address[]"
        },
        {
          "name": "_minValidators",
          "type": "uint8"
        }
      ],
      "payable": true,
      "stateMutability": "payable",
      "type": "constructor"
    },
    {
      "payable": true,
      "stateMutability": "payable",
      "type": "fallback"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": true,
          "name": "previousOwner",
          "type": "address"
        },
        {
          "indexed": true,
          "name": "newOwner",
          "type": "address"
        }
      ],
      "name": "OwnershipTransferred",
      "type": "event"
    },
    {
      "constant": false,
      "inputs": [],
      "name": "validate",
      "outputs": [],
      "payable": false,
      "stateMutability": "nonpayable",
      "type": "function"
    }
  ],

  init: function() {
    return App.initWeb3();
  },

  initWeb3: function() {
    if (typeof web3 !== "undefined") {
      // Browser MetaMask
      App.web3Provider = web3.currentProvider;
      web3 = new Web3(web3.currentProvider);
    } else {
      App.web3Provider = new Web3.providers.HttpProvider(
        "http://127.0.0.1:8545"
      );
      web3 = new Web3(App.web3Provider);
    }

    web3.eth.getAccounts( (err, res) => {
        App.accounts = res;
    });

    return App.bindEvents();
  },

  bindEvents: function() {
    if ($("#deployButton").length) {
      $("#deployButton").click(App.handleDeploy);
    }
    if ($("#validateButton").length) {
      $("#validateButton").click(App.handleValidate);
    }
  },

  handleDeploy: function(event) {
    event.preventDefault();

    const accounts = App.getAccounts();

    const recipients = accounts;

    var percentages = [];
    var left = 100;

    for (index in accounts) {
      percentage = Math.ceil(Math.random() * (left - 1) + 1);
      if (index == accounts.length - 1) {
        percentage = left;
      } else {
        left -= percentages;
      }

      percentages.push(percentage);
    }

    console.log(percentages);

    const validators = accounts;
    const minValidators = accounts.length;

    App.createContract(recipients, percentages, validators, minValidators);
  },

  handleValidate: function(event) {
    event.preventDefault();
    var contractAddress = $("#contractAddress").val();

    App.validateContract(contractAddress);
  },

  createContract: function(recipients, percentages, validators, minValidators) {
        const contract = web3.eth.contract(App.abi);

        recipients = recipients.filter(
            function(value) {
                return value.length > 0;
            }
        );

        percentages = percentages.filter(
            function(value) {
                return value > 0;
            }
        );

        validators = validators.filter(
            function(value) {
                return value > 0;
            }
        );

        contract.new(
            recipients,
            percentages,
            validators,
            minValidators,
            {
                from: web3.eth.accounts[0],
                data: App.bytecode,
                gas: 4500000,
                value: web3.toWei(1, "ether")
            },
            (err, res) => {
                if (err) {
                    console.log(err);
                    return;
                }

                console.log(res.transactionHash);

                if (res.address) {
                    $('#contractAddress').val(res.address);
                    console.log('Contract address: ' + res.address);
                }
            }
        );
  },

  validateContract: function(contractAddress) {
    if (!contractAddress.length) {
      return;
    }

    const contract = web3.eth.contract(App.abi);

    const contractInstance = contract.at(contractAddress);

    contractInstance.validate((err, res) => {
      if (err) {
        console.log(err);
        return;
      }

      console.log(res);
    });
  },

  isAddress: function(address) {
    return web3.utils.isAddress(address);
  },

  getAccounts: function() {
    return App.accounts;
  }
};
